<main class="form-signin w-100 m-auto">
  <form pb-autologin="true" autocomplete="off">
    <img class="mb-4" src="/docs/5.2/assets/brand/bootstrap-logo.svg" alt="" width="72" height="57">
    <h1 class="h3 mb-3 fw-normal">Please sign in</h1>

    <div class="form-floating">
      <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com" pb-role="username">
      <label for="floatingInput">Email address</label>
    <passwordboxicon style="position: absolute; background: url(&quot;data:image/png;base64,&quot;) right center no-repeat; width: 30px; height: 58px; z-index: auto; visibility: visible; top: 0px; left: 270px;" pb-icon="username" icon-type="main"></passwordboxicon></div>
    <div class="form-floating">
      <input type="password" class="form-control" id="floatingPassword" placeholder="Password" pb-role="password">
      <label for="floatingPassword">Password</label>
    <passwordboxicon style="position: absolute; background: url(&quot;data:image/png;base64,&quot;) right center no-repeat; width: 30px; height: 58px; z-index: auto; visibility: visible; top: 0px; left: 270px; cursor: pointer;" pb-icon="password" icon-type="main"></passwordboxicon></div>

    <div class="checkbox mb-3">
      <label>
        <input type="checkbox" value="remember-me"> Remember me
      </label>
    </div>
    <button class="w-100 btn btn-lg btn-primary" type="submit" pb-role="submit">Sign in</button>
    <p class="mt-5 mb-3 text-muted">© 2017–2022</p>
  </form>
</main>